import React, {Component} from 'react';
import Question from './Question';
import {withStyles, fade} from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import Divider from '@material-ui/core/Divider';

const styles = {
    title:{
        display: "flex",
        fontFamily: "Rockwell",
        fontSize: "50px",
        justifyContent: "center"
    },
    border: {
        borderRadius: "10%"
    }
        
}

class FAQ extends Component {
    constructor(props) {
        super(props)
        this.state = {
            questionText: [
                <bold>Understanding Mental Illness</bold>,
                <bold>Understanding Emotions</bold>,
                <bold>Dealing with the Pandemic</bold>,
                <bold>Healthy Habits</bold>
            ],
            topic:[
                 [  
                <bold>Signs/Symptoms</bold>, //UMI
                <bold>Causes</bold>,
                <bold>Solutions</bold>
                ],
                [
                    <bold>Depression</bold>, //UE
                    <bold>Anxiety</bold>,
                    <bold>Stress</bold>,
                    <bold>Anger</bold>,
                    <bold>Bipolar Disorder</bold>,
                    <bold>Eating Disorder</bold>
                ],
                [
                    <bold>Introduction</bold>, //DWTP
                    <bold>Healthy Practises</bold>,
                    <bold>Stay in Touch</bold>,
                    <bold></bold>
                ],
                [
                    <bold>Maintain a Healthy Diet</bold>, //HH
                    <bold>Stick to a Sleeping Schedule</bold>,
                    <bold>Socialize with Friends and Family</bold>
                ]
            ],
            answers:[[
                <bold></bold>,
                <bold></bold>,
                <bold></bold>
            ], [], [], []
            ],
            open: [false, false, false, false]
        }
        this.handleOpen = this.handleOpen.bind(this);
    }

    handleOpen(index) {
        let newOpenState = [...this.state.open];
        newOpenState[index] = !this.state.open[index];
        this.setState({open: newOpenState}, () => console.log(this.state.open));
    }

    render() {
        const {classes} = this.props;
        const {question, topic, answers, open} = this.state;
        return(
            <div>
                <h2 className={classes.title}>Frequently Asked Questions</h2>
                <List>
                    {this.state.questionText.map((question, i) => (
                        <Question questionText={question} topicText={topic[i]} answersText={answers[i]} open={open} handleOpen={this.handleOpen} index={i}/>
                    ))}
                </List>
            </div> 
        )
    }
}
export default withStyles(styles)(FAQ);
