import React, {Component} from 'react';
import {withStyles} from '@material-ui/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import Divider from '@material-ui/core/Divider';
import NavigateNextIcon from '@material-ui/icons/NavigateNext';

const styles = {
    list:{
        width: "95%",
        fontFamily: "Rockwell",
        margin: "auto",
        padding: "10px",
        textAlign: "left"
        },
    content: {
        "& ul, li": {
            width: "95%"
        },
        fontFamily: "Mulish",
        width: "90%",
        margin: "auto",
        textAlign: "left"
    }
}

class Question extends Component {
    constructor(props) {
        super(props);
        this.handleClick = this.handleClick.bind(this);
    }
    handleClick() {
        this.props.handleOpen(this.props.index);
    }
    render(){
        const {classes, questionText, topicText, answersText, open} = this.props;
        return(
            <div>
                <ListItem button divider onClick={this.handleClick}>
                    <ListItemText><p className={classes.list}>{questionText}</p></ListItemText>
                    {open[this.props.index] ? <ExpandLess /> : <ExpandMore />}
                </ListItem>
              <Collapse in={open[this.props.index]} timeout="auto" unmountOnExit>
                    {topicText.map((tpc, k) => (
                        <div>
                            <h2 className= {classes.list}>{tpc}</h2>
                            <p className={classes.content}>{answersText[k]}</p>
                        </div>
                    ))}
                </Collapse>
            </div>
       )
    }
}

export default withStyles(styles)(Question);
