import React, {Component} from 'react';
import logo from './logo.svg';
import Counter from './Counter';
import Journal from './Journal'; 
import './App.css';


class App extends Component {
  constructor(props) {
    super(props);
    const savedJournals = JSON.parse(window.localStorage.getItem("journals")) // get the previously saved file of "journals"
    this.state = {
        journals: [
            {date: "1 January 2020", highlights: "Today I ate food", mood: "happy"}
        ]
    }
}
  saveJournal(newJournal){
    this.setState({journals: [...this.state.journals, newJournal]}, this.syncLocalStorage) // ... is for appending the previous items in the array
}
  syncLocalStorage(){
    window.localStorage.setItem("journals", JSON.stringify(this.state.journals)) // update the "journals" file, uses stringify since JSON does not accept any type other than strings.
}
  render() {
    return (
        <div className="App">
          {/*<Counter count = {1} name = "Chrisbry"/>*/}
          <Journal saveJournal/>
        </div>
      );
    }

}
export default App;
