import React, {Component} from 'react';

class Counter extends Component {
    constructor(props){
        super(props);
        this.state = {
            currentClicks: 0
        }
        this.addClicks = this.addClicks.bind(this);
    }
    addClicks() { //functions don't need to be determined with "function" in a class
        this.setState({currentClicks: this.state.currentClicks + 1})
    }
    render(){
        const {count, name} = this.props;
        return(
            <div>
                <h1>This is from the Counter component and it is made by {name}</h1>
                <p>The count is: {count}</p>
                <h4>You clicked {this.state.currentClicks} times</h4>
                <button onClick = {this.addClicks}>Click me</button>
            </div>
        );
    }
}

export default Counter;
