import React, {Component} from 'react';
import {withStyles, fade} from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import Divider from '@material-ui/core/Divider';

const styles = {
    title:{
        display: "flex",
        fontFamily: "Rockwell",
        fontSize: "50px",
        justifyContent: "center"
    },
    list:{
        width: "100%",
        fontFamily: "Rockwell",
        textAlign: "center"
    },
    border: {
        borderRadius: "10%"
    }
}

class FAQ extends Component {
    constructor(props) {
        super(props)
        this.state = {
            question: [
                <bold>Understanding Mental Illness</bold>,
                <bold>Understanding Emotions</bold>,
                <bold>Dealing with the Pandemic</bold>,
                <bold>Healthy Habits</bold>
            ],
            topic:[
                 [  
                <bold>Signs/Symptoms</bold>, //UMI
                <bold>Causes</bold>,
                <bold>Solutions</bold>
                ],
                [
                    <bold>Depression</bold>, //UE
                    <bold>Anxiety</bold>,
                    <bold>Stress</bold>,
                    <bold>Anger</bold>,
                    <bold>Bipolar Disorder</bold>,
                    <bold>Eating Disorder</bold>
                ],
                [
                    <bold>Introduction</bold>, //DWTP
                    <bold>Healthy Practises</bold>,
                    <bold>Stay in Touch</bold>,
                    <bold></bold>
                ],
                [
                    <bold>Maintain a Healthy Diet</bold>, //HH
                    <bold>Stick to a Sleeping Schedule</bold>,
                    <bold>Socialize with Friends and Family</bold>
                ]
            ],
            open: false,
            answers:[[
                <bold></bold>,
                <bold></bold>,
                <bold></bold>
            ], [], [], []
            ]
        }
    }

    handleClick = () =>{
        this.setState({open: !this.state.open});
    }

    render() {
        const {classes} = this.props;
        const {question, topic, answers, open} = this.state;
        return(
            <div>
                <h2 className={classes.title}>Frequently Asked Questions</h2>
                <List className={classes.border}>
                        {question.map((qstn, i) => (
                            <div>
                                <ListItem button divider onClick={this.handleClick}>
                                    <ListItemText><p className={classes.list}>{qstn}</p></ListItemText>
                                    {open ? <ExpandLess /> : <ExpandMore />}
                                </ListItem>
                                <Collapse in={open} timeout="auto" unmountOnExit>
                                    {topic[i].map((tpc, k) => (
                                            <div>
                                                <h2>{tpc}</h2>
                                                <h3>{answers[i][k]}</h3>
                                            </div>
                                    ))}
                                </Collapse>
                            </div>
                        ))}
                    
                </List>
            </div> 
        )
    }
}
export default withStyles(styles)(FAQ);
